const front = document.querySelector('.front')
const showcase = document.querySelectorAll('.showcase')
const header= document.querySelector('header')
const hi = document.querySelector('.hi')
const frontend = document.querySelector('.frontend')
const desc = document.querySelector('.desc')
const showcaseButtons = document.querySelector('.showcase-buttons')
const icons = document.querySelector('.highlight-icons')
const mouse = document.querySelector('.mouse-container')
const bgLeft = document.querySelector('.backdrop-left-home')
const bgRight = document.querySelector('.backdrop-right-home')
const graphicsBg = document.querySelector('.graphics-bg')
const vexel = document.querySelector('.vexel')

const tl = new TimelineMax();

tl.fromTo(front, 1.5, {x: '0%', display: 'flex'}, {x: "100%", display: 'none', ease: Power2.easeInOut})
  .fromTo(showcase, 1, { scale: '1.2', y: "-4%"}, {scale: '1', y: "0%", ease: Power2.easeInOut})
  .fromTo(hi, 1, {y: '70%'}, {y: '0%', ease: Power2.easeInOut})
  .fromTo(frontend, 1, {opacity: 0, y: "20px"}, {opacity: 1, y:"0px", ease: Power2.easeInOut}, "-=0.7")
  .fromTo(desc, 1, {opacity: 0}, {opacity: 1 , ease: Power2.easeInOut}, "-=0.7")
  .fromTo(showcaseButtons, 1, {opacity: 0}, {opacity: 1 , ease: Power2.easeInOut}, "-=0.7")
  .fromTo(header, 1, {opacity: 0}, {opacity: 1, ease: Power2.easeInOut}, "-=2") 
  .fromTo(icons, 1, {opacity: 0}, {opacity: 1, ease: Power2.easeInOut}, "-=1.5")
  .fromTo(mouse, 1, {opacity: 0}, {opacity: 1, ease: Power2.easeInOut}, "-=1.5")
  .fromTo(graphicsBg, 1, {opacity: 0}, {opacity: 1, ease: Power2.easeInOut}, "-=1.5")
  .fromTo(bgLeft , 1, {x: "-100%"}, {x: "0%", ease: Power2.easeInOut}, "-=1.5")
  .fromTo(bgRight , 1, {x: "100%", display: "none"}, {display: "block", x: "0%", ease: Power2.easeInOut}, "-=1.5")